import React, { useState } from 'react';
import Modal from 'react-modal';
import { Form, Container, Input, Button, Row, Col, Card, CardBody, FormGroup, Label, InputGroup, InputGroupAddon } from 'reactstrap';
import styled from 'styled-components';
class FormList extends React.Component {
    constructor(props) {
        super(props)
        this.state = { item: '', listItem: [], modalShow: false }
    }
    submitItem = (event) => {
        event.preventDefault()
        if (this.state.item != '')
            this.setState({ item: '', listItem: [...this.state.listItem, this.state.item] })
    }
    removeItem(pos) {
        return ((event) => {
            var tempItem = [...this.state.listItem];
            tempItem.splice(pos, 1);
            this.setState({ listItem: tempItem });
            event.preventDefault()
        });
    }

    showModal = () => {
        console.log('show modal')
        this.setState({ modalShow: true })
    }

    dissmisModal = () => {
        this.setState({ modalShow: false })
    }

    inputListener = (event) => { this.setState({ item: event.target.value }) }
    render() {
        const StyleItem = styled.div`
            height: 50px;
            background-color: #F3E5F5;
            border-bottom: 2px solid grey;
            border-radius: 10px;
            margin-top: 10px;
            padding-top: 10px;
            padding-left:10px;
            padding-right:10px;
        `;


        return (
            <div>
                <Container>
                    <Row>
                        <Col />
                        <Col lg="6">
                            <h3><u>TODO List</u></h3>
                            <Card>
                                <CardBody>
                                    <Form onSubmit={this.submitItem} >
                                        <FormGroup>
                                            <InputGroup>
                                                <Input placeholder="Ingin menambahkan sesuatu ?" value={this.state.item} onChange={this.inputListener} />
                                                <InputGroupAddon addonType="append">
                                                    <Button color="primary">Tambah</Button>
                                                </InputGroupAddon>
                                            </InputGroup>
                                        </FormGroup>
                                    </Form>
                                    <hr />
                                    {this.state.listItem.map((item, index) =>
                                        <StyleItem key={index} onClick={this.showModal} >
                                            <Label for="btnRemove" >{item}</Label>
                                            <Button id="btnRemove" close onClick={this.removeItem(index)} />
                                        </StyleItem>
                                    )}
                                </CardBody>
                            </Card>
                        </Col>
                        <Col />
                    </Row>
                </Container>

                <Modal 
                isOpen={this.state.modalShow} 
                onRequestClose={this.hideModal}
                contentLabel="Example Modal">
<div>I am a modal</div>
