import React from 'react';
import { Form, Container, Input, Button, Row, Col, Card, CardBody, FormGroup, Label, InputGroup, InputGroupAddon } from 'reactstrap';
import styled from 'styled-components';
class FormList extends React.Component{
    constructor(props){
        super(props)
        this.state = { item:'', listItem:[]}
    }
    submitItem = (event) => {
        event.preventDefault()
        if (this.state.item != '')
            this.setState({ item:'', listItem: [...this.state.listItem, this.state.item] })
    }
    removeItem (pos) {
        return((event)=>{
            var tempItem = [...this.state.listItem]; 
            tempItem.splice(pos, 1);
            this.setState({listItem: tempItem});
            event.preventDefault()
        });
    }
    inputListener = (event) => { this.setState({ item: event.target.value }) }
    render(){
        const IconAdd = `
            width:30px;
        `;
        const StyleItem = styled.div`
            height: 50px;
            background-color: #F3E5F5;
            border-bottom: 2px solid grey;
            border-radius: 10px;
            margin-top: 10px;
            padding-top: 10px;
            padding-left:10px;
            padding-right:10px;
        `;
        return(
            <div>
                <Container>
                    <Row>
                        <Col/>
                        <Col lg="8"> 
                            <h3>
                                <u>TODO List</u>
                            </h3>
                            <Card>
                                <CardBody>
                                    <div>
                                        <Form onSubmit={this.submitItem} >
                                            <FormGroup>
                                            <InputGroup>
                                                <Input placeholder="Ingin menambahkan sesuatu ?" value={this.state.item} onChange={this.inputListener}/>
                                                <InputGroupAddon addonType="append">
                                                <Button style={IconAdd} color="secondary"><img src="https://www.kindpng.com/picc/m/543-5432090_transparent-addition-symbol-clipart-icon-button-add-png.png"/></Button>
                                                </InputGroupAddon>
                                            </InputGroup>
                                            </FormGroup>
                                        </Form>
                                    </div>

                                    <hr/>
                                    <div>
                                        { this.state.listItem.map((item, index) => 
                                                <StyleItem key={index}>
                                                    <Form onSubmit={this.removeItem(index)}>
                                                        <FormGroup>
                                                            <Label for="btnRemove">{item}</Label>
                                                            <Button id="btnRemove" close type="submit"/>
                                                        </FormGroup>
                                                    </Form>
                                                </StyleItem>
                                            )}
                                    </div>
                                </CardBody>
                            </Card>
                        </Col>
                        <Col/>
                    </Row>
                </Container>
            </div>
        );
    }
}
export default FormList
